CREATE TABLE "accounts" ("mobile" varchar(255),"no" varchar(255),"reg_time" datetime,"last_login_time" datetime,"id" varchar(255),"status" integer,"ip" varchar(255),"password" varchar(255) , avatar text, PRIMARY KEY ("id"));
